css:
body { font-family: Arial, sans-serif; font-size: 12pt; }
    .agreement-title { text-align: center; font-weight: bold; font-size: 16pt; text-decoration: underline; margin-top: 20px; margin-bottom: 20px; }
    .section-title { font-weight: bold; margin-top: 15px; }
    .signature-section { margin-top: 40px; display: flex; justify-content: space-between; }
    .signature-block { width: 45%; }
    .note-section { margin-top: 30px; font-size: 10pt; color: #444; }
    .print-format-footer { width: 100%; text-align: right; font-size: 10pt; position: fixed; bottom: 0; right: 0; padding: 10px 20px; }
    .page-break { page-break-before: always; }
    ul { margin-top: 0; }
