html:
{% set employee = frappe.get_doc("Employee", doc.employee) %}
{% set date = frappe.utils.formatdate(doc.start_date, "MMM yyyy") %}

<table style="padding:0px !important;margin:0px !important;border:1px solid #000;border-bottom:none;width:100%;">
    <tr>
        <td style="width:25% !important;border-bottom:none !important;" >
            {% if doc.company %}
            {% set company = frappe.get_doc("Company",doc.company) %}
            {% if company.company_logo %}
            <img src="{{ frappe.get_url() }}/{{ company.company_logo }}">
            {% endif %}
            {%endif %}

        </td>
        <td>
                <div style="padding-right:100px;text-align:center;">
               <p style="font-size:18px; font-weight:bold;">{{ doc.company }}</p>
        {% set company = frappe.get_doc("Company",doc.company) %}
        {% set company_address = frappe.get_all("Dynamic Link", filters={"parenttype":"Address","link_name":company.name,"link_doctype":"Company"}, fields=["parent"])%}
        {% if company_address %}
    {% set address = frappe.get_doc('Address', company_address[0].parent) %}

    {% if address.address_line1 %}
        {{ address.address_line1 }}
    {% endif %}

    {% if address.address_line2 %}
        {{ address.address_line2 }}<br>
    {% endif %}

    {% set parts = [] %}
    {% if address.city %}{% set _ = parts.append(address.city) %}{% endif %}
    {% if address.state %}{% set _ = parts.append(address.state) %}{% endif %}
    {% if address.pincode %}{% set _ = parts.append(address.pincode) %}{% endif %}

    {% if parts|length %}
        {{ parts | join(', ') }}
    {% endif %}
{% endif %}
<div style="text-align:center;font-size:16px;"><b>Pay Slip for the month of {{date or ""}} </b></div>
            </div>
        </td>
    </tr>
</table>

<table style="padding:0px !important;margin:0px !important;border:1px solid #000;width:100%;">
    <tr>
        <td style="width:20%;">Name:</td>
        <td style="width:30%;border-right:1px solid #000;text-align:left;">{{ frappe.db.get_value("Employee", doc.employee, "employee_name") or "" }}</td>
        <td style="width:25%;">Bank Name:</td>
        <td style="width:25%;text-align:left;">{{ employee.bank_name or "" }}</td>
    </tr>
    <tr>
        <td>Date of Joining:</td>
        <td style="border-right:1px solid #000;text-align:left;">{{ frappe.format_date(employee.date_of_joining,"dd-MM-yyyy") or "" }}</td>
        <td>Bank Account No:</td>
        <td style="text-align:left;">{{ employee.bank_ac_no or "" }}</td>
    </tr>
    <tr>
        <td>Designation:</td>
        <td style="border-right:1px solid #000;text-align:left;">{% if doc.designation %}
                {{ frappe.db.get_value("Designation",doc.designation,"designation_name") or "" }}
                {% endif %}</td>
        <td>PF No:</td>
        <td style="text-align:left;">{{ employee.provident_fund_account or "" }}</td>
    </tr>
    <tr>
        <td>Department:</td>
        <td style="border-right:1px solid #000;text-align:left;">{% if doc.department %}
                {{ frappe.db.get_value("Department",doc.department,"department_name") or "" }}
                {% endif %}</td>
        <td>UAN:</td>
        <td style="text-align:left;">{{ employee.custom_uan_number or "" }}</td>
    </tr>
    <tr>
        <td>Location:</td>
        <td style="border-right:1px solid #000;text-align:left;">{% if employee.custom_work_location %}
    {% set address = frappe.get_doc("Address", employee.custom_work_location) %}
    {% if address.address_title %}
{{ address.address_title }}
    {% endif %}
{% endif %}</td>
        <td>ESI No:</td>
        <td style="text-align:left;">{{ employee.custom_esi_number or "" }}</td>
    </tr>
    <tr>
        <td>Effective Work Days:</td>
        <td style="border-right:1px solid #000;text-align:left;">{{ doc.total_working_days or "" }}</td>
        <td>PAN No:</td>
        <td style="text-align:left;">{{ employee.pan_number or "" }}</td>
    </tr>
    <tr>
        <td>Days In Month:</td>
        <td style="border-right:1px solid #000;text-align:left;">{{ doc.payment_days or "" }}</td>
        <td>LOP:</td>
        <td style="text-align:left;">{{ doc.custom_lop_days or 0 }}</td>
    </tr>
    <tr>
        <td>Monthly CTC:</td>
        <td style="border-right:1px solid #000;text-align:left;">{{ doc.rounded_total or "" }}</td>
        <td>Currency:</td>
        <td style="text-align:left;">{{ employee.salary_currency or "" }}</td>
    </tr>
</table>
<table style="padding:0px !important;margin:0px !important;border:1px solid #000;border-top:none;width:100%;">
    <tr style="border:1px solid #000;border-top:none;">
        <td style="width:20%;font-weight:bold;text-align:left;">Earnings</td>
        <td style="width:15%;font-weight:bold;text-align:right;">Full</td>
        <td style="width:15%;font-weight:bold;border-right:1px solid #000;text-align:right;">Actual</td>
        <td style="width:30%;font-weight:bold;text-align:left;">Deductions</td>
        <td style="width:20%;font-weight:bold;text-align:right;">Actual</td>
    </tr>
    {% set earnings = doc.earnings %}
    {% set deductions = doc.deductions %}
    {% set max_rows = [earnings|length, deductions|length]|max %}
    {% for i in range(max_rows) %}
    <tr>
        <td style="text-align:left;">{{ earnings[i].salary_component if earnings|length > i else "" }}</td>
        <td style="text-align:right;">&nbsp;</td>
        <td style="border-right:1px solid #000;text-align:right;">{{ "{:,.2f}".format(earnings[i].amount) if earnings|length > i else "" }}</td>
        <td style="text-align:left;">{{ deductions[i].salary_component if deductions|length > i else "" }}</td>
        <td style="text-align:right;">{{ "{:,.2f}".format(deductions[i].amount) if deductions|length > i else "" }}</td>
    </tr>
    {% endfor %}
    <tr style="border:1px solid #000;font-weight:bold">
        <td>Total Earnings:</td>
        <td>&nbsp;</td>
        <td style="border-right:1px solid #000;text-align:right;">Rs. {{ "{:,.2f}".format(doc.gross_pay or 0) }}</td>

        <td>Total Deductions:</td>
        <td style="text-align:right;">Rs. {{ "{:,.2f}".format(doc.total_deduction or 0) }}</td>
    </tr>
    <tr style="border:1px solid #000;">
        <td colspan="5" class="right">Net Pay for the month ( Total Earnings - Total Deductions): <b>&nbsp;&nbsp;&nbsp;{{ "{:,.2f}".format(doc.net_pay or 0) }} &nbsp;</b> ({{ frappe.utils.money_in_words(doc.net_pay or 0) }} )</td>
    </tr>
</table>
{% if doc.leave_details %}
<table class="table p-0 m-0" style="border:1px solid #000 !important;border-top:none !important;">
    <thead>
        <tr>
            <td style="border:1px solid #000 !important;border-top:none !important;">Leave Code</td>
            <td style="border:1px solid #000 !important;border-top:none !important;">Used in this Month</td>
            <td style="border:1px solid #000 !important;border-top:none !important;">Balance</td>
        </tr>
    </thead>
    <tbody>
        {% for row in doc.leave_details %}
            <tr>
                {% if row.leave_type %}
    {% set leave_doc = frappe.get_doc("Leave Type", row.leave_type) %}
    {% if leave_doc.custom_leave_type_abbr %}
        {% set leave_code = leave_doc.custom_leave_type_abbr %}
    {% else %}
        {% set ns = namespace(leave_code = "") %}
        {% for word in row.leave_type.split(" ") %}
            {% set ns.leave_code = ns.leave_code + word[0] %}
        {% endfor %}
        {% set default_leave_code = ns.leave_code.upper() %}
    {% endif %}
{% endif %}
                <td style="border:1px solid #000 !important;">{{ leave_code or default_leave_code or "" }}</td>
                <td style="border:1px solid #000 !important;">{{ row.used_leaves or 0 }}</td>
                <td style="border:1px solid #000 !important;">{{ row.available_leaves or 0 }}</td>
            </tr>
        {% endfor %}
    </tbody>
</table>
{% endif %}
</div>
