css:
.print-heading {
        text-align: left;
        margin-bottom: 20px;
        border-bottom:none;
    }

    .leave-table {
        width: 100%;
        border-collapse: collapse;
        margin-top:10px !important;
    }
    .leave-table th, .leave-table td {
        border: 1px solid #000 !important;
        padding: 5px;
        text-align: center;
        font-size: 8pt;
    }
    .leave-table th {
        color: black !important;
        font-weight: bold;
    }
    .amount-column {
        text-align: right !important;
    }
    .total-row {
        font-weight: bold;
    }
    .net-salary {
        margin-top: 15px;
        border: 1px solid #ddd;
        padding: 10px;
    }
    .net-salary-amount {
        font-weight: bold;
        font-size: 12pt;
    }
    .salary-slip {
            width: 100%;
            font-family: Arial, Helvetica, sans-serif;
            font-size: 12px;
            border-collapse: collapse;
        }
        .salary-slip th {
            background-color: #f2f2f2;
            padding: 8px;
            text-align: left;
            font-weight: bold;
            border-right: 1px solid #ddd;
        }
        .salary-slip td {
            padding: 8px;
            border-right: 1px solid #ddd;
            vertical-align: top;
        }
        .inner-table {
            width: 100%;
            border-collapse: collapse;
        }
        .inner-table th, .inner-table td {
            border: none;
            text-align: left;
            margin:0px;
            padding:0px !important;
            padding-bottom:5px !important;

        }
        .amount-column {
            text-align: right;
            width: 100px;
        }
        .section-title {
            font-weight: bold;
            margin-bottom: 10px;
            font-size: 14px;
        }
